import { useState } from "react";
import FlashMsg from "../Partials/FlashMsg";
import "./Overlay.css"
import "./PantryList.css"

const categories = ["Vegetables", "Fruits", "Grains Pasta", "Dairy", "Meat Poultry", "Fish Eggs", "Fats Oils", "Condiments", "Freezer", "Baking", "Nuts Snacks", "Miscellaneous", "Saved Items"];
const twoWordCats = { "Grains Pasta": "Grains & Pasta", "Fish Eggs": "Fish & Eggs", "Fats Oils": "Fats & Oils", "Meat Poultry": "Meat & Poultry", "Nuts Snacks": "Nuts & Snacks" };
const items = [];


function PantryPage(props) {
    const [enteredItem, setEnteredItem] = useState('');

    const itemChangeHandler = (event) => {
        setEnteredItem(event.target.value);
    }

    const submitHandler = (event) => {
        event.preventDefault();

        const itemData = {
            name: enteredItem
        }

        props.onAddItem(itemData);

        setEnteredItem('');
    };

    return (
        <section class="pantry-page-cont">
            <div class="overlay"></div>
            <section class="container pantry-menu-cont page p-0 overflow-auto">
                <FlashMsg />
                {categories.map((cat) => (
                    <section class={cat.toLowerCase() + "-section pantry-category"}>test</section>
                ))}
            </section >
        </section >
    );
}

export default PantryPage;
