import { useState } from "react";
import FlashMsg from "../Partials/FlashMsg";
import "./Overlay.css"
import "./PantryList.css"

const categories = ["Vegetables", "Fruits", "Grains Pasta", "Dairy", "Meat Poultry", "Fish Eggs", "Fats Oils", "Condiments", "Freezer", "Baking", "Nuts Snacks", "Miscellaneous", "Saved Items"];
const twoWordCats = { "Grains Pasta": "Grains & Pasta", "Fish Eggs": "Fish & Eggs", "Fats Oils": "Fats & Oils", "Meat Poultry": "Meat & Poultry", "Nuts Snacks": "Nuts & Snacks" };
const items = [{name:"Tomato", category: "Vegetables", inStock: true}];


function PantryPage() {
    const [items, setItems] = useState([
        {"Vegetables":''},
        {"Fruits":''},
        {"Grains Pasta":''},
        {"Dairy":''},
        {"Meat Poultry":''},
        {"Fish Eggs":''},
        {"Fats Oils":''},
        {"Condiments":''},
        {"Freezer":''},
        {"Baking":''},
        {"Nuts Snacks":''},
        {"Miscellaneous":''},
        {"Saved Items":''},
    ]);
    const [category, setCategory] = useState('');

    const itemChangeHandler = (event) => {
        const cat = event.target.parentElement.parentElement.parentElement.firstElementChild.innerText;
        setCategory(cat)
        const index = categories.indexOf(cat);
        // console.log(index);
        items[index][cat] = event.target.value; 
        // console.log(enteredItems[index][cat])
        setItems(items);
        // console.log(enteredItems);
    }

    const newItem = {
        // name: enteredItems[categories.indexOf(enteredItemCat)][enteredItemCat],
        category: category,
        inStock: true
    }

    const submitHandler = (event) => {
        event.preventDefault();
        const index = categories.indexOf(category)
        items.push(newItem);
        // enteredItems[index][enteredItemCat] = '';
        // setEnteredItems(enteredItems);
    };

    return (
        <section className="pantry-page-cont">
            <div className="overlay"></div>
            <section className="container pantry-menu-cont page p-0 overflow-auto">
                <FlashMsg />
                {categories.map((cat) => (
                    <section className={cat.toLowerCase() + "-section pantry-category"}>
                        <div className="category-col category-row d-flex justify-content-center align-items-center">
                            {cat in twoWordCats ? twoWordCats[cat] : cat}
                            <img src={"https://res.cloudinary.com/meal-creator/image/upload/v1662276054/icons/" + cat.toLowerCase() + ".png"} className="category-icon" />
                        </div>
                        <div className="category-row justify-content-start d-flex align-items-center p-0">
                            <form action={"/pantry/" + cat} method="post" className="form-container" onSubmit={submitHandler}>
                                <button type="submit" disabled style={{ display: "none" }} aria-hidden="true"></button>
                                <input type="text" name="name" placeholder="Add Item" className="search-category" value={items[cat]} onChange={itemChangeHandler} />
                                <button type="submit" id="new-item-pantry-btn" className="btn btn-light add-button add-pantry-button d-flex justify-content-center align-items-center p-0"></button>
                            </form>
                        </div>
                        {
                            items.map((item) => (
                                (item.category === cat && cat !== "Saved Items" && item.inStock) ?
                                    < div className="row list-item-bar m-0" title={cat.toLowerCase() + "-section pantry-category"} >
                                        <form className="d-flex form-container" action={"/pantry/move/" + item.name} method="post">
                                            <div className="col-sm-1 d-flex justify-content-begin align-items-center p-0 check-col">
                                                <input type="image" className="btn btn-light list-check" value="" />
                                            </div>
                                        </form>
                                        <form className="d-flex form-container" action={"/pantry/" + item._id + "?_method=PATCH"} method="post">
                                            <button type="submit" disabled style={{display: "none"}} aria-hidden="true"></button>
                                            <div className="col-11 p-0 pantry-input-field">
                                                <input type="text" name="name" className="item-edit" value={item.name} placeholder={item.name} />
                                            </div>
                                            <div className="col-1 d-flex justify-content-start align-items-center p-0">
                                                <input type="submit" id="pantry-enter-btn" className="btn btn-light enter-btn" value="" />
                                            </div>
                                        </form>
                                        <form className="form-container" action={"/pantry/" + item._id + "?_method=DELETE"} method="post">
                                            <div className="col-sm-1 d-flex justify-content-begin align-items-center p-0">
                                                <input type="submit" className="btn btn-light trash-btn" value="" />
                                            </div>
                                        </form>
                                    </div>
                                    : (cat === "Saved Items" && !item.inStock) ?
                                    < div className="row list-item-bar m-0" title="saved items-section pantry-category" >
                                        <form className="d-flex form-container" action={"/pantry/move-back/" + item.name} method="post">
                                            <div className="col-sm-1 d-flex justify-content-begin align-items-center p-0 check-col">
                                                <input type="image" className="btn btn-light list-uncheck" value="" />
                                            </div>
                                        </form>
                                        <form className="d-flex form-container" action={"/pantry/" + item._id + "?_method=PATCH"} method="post">
                                            <button type="submit" disabled style={{display: "none"}} aria-hidden="true"></button>
                                            <div className="col-sm-10 p-0 pantry-input-field">
                                                <input type="text" name="name" id="saved-item-edit" className="item-edit" value={item.name} placeholder={item.name} />
                                            </div>
                                            <div className="col-sm-1 d-flex justify-content-end align-items-center p-0">
                                                <input type="submit" id="pantry-enter-btn" className="btn btn-light enter-btn-saved" value="" />
                                            </div>
                                        </form>
                                        <form className="form-container" action={"/pantry/" + item._id + "?_method=DELETE"} method="post">
                                            <div className="col-sm-1 d-flex justify-content-center align-items-center p-0">
                                                <input type="submit" className="btn btn-light trash-btn-saved" value="" />
                                            </div>
                                        </form>
                                        <form className="form-container" action={"/pantry/move-to-cart/" + item.name} method="post">
                                            <div className="col-sm-1 d-flex align-items-center p-0">
                                                <input type="submit" className="btn btn-light cart-btn" value="" />
                                            </div>
                                        </form>
                                    </div>
                                    : null
                            ))}
                    </section>
                ))}
            </section >
        </section >
    );
}


export default PantryPage;
